def PolicyNumberExtrator(sheet):
    Policy_Number = None
    POLICY_NO_STATUS = None
    for i in range(sheet.nrows):   
        try:                
            print(sheet.row_values(i))
            text = str(sheet.row_values(i))
            Policy_Number = ''
            if 'policy' in text.lower():
                PolicyNumberPattern = r"([Q])\d{4}-OA\d{2}-\d{7}([A-Z])"
                prog = re.compile(PolicyNumberPattern, re.IGNORECASE)
                mo = prog.search(text)   
                if mo is not None:                                
                    Policy_Number = mo.group()    
                    POLICY_NO_STATUS = 'POLICY_NUMBER FOUND'
                else:
                    POLICY_NO_STATUS = 'POLICY_NUMBER NOT FOUND'
                break
        except Exception:
            file_object.write(str(datetime.now())[0:19]+"|"+str(traceback.format_exc())+str("\n"))

    return Policy_Number, POLICY_NO_STATUS


